package com.greedy.section02.assignmnet.operator;

public class Application1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num =12;
		System.out.println("num : " + num);
		
		num = num + 3;
		System.out.println("num : " + num);
		
		num += 3;
		System.out.println("num : " + num);
		
		num -= 5; 
		System.out.println("num : " + num);
		
		num *= 2;
		System.out.println("num : " + num);
		
		num /= 2;
		System.out.println("num : " + num);
		
		num %= 2;
		System.out.println("num : " + num);
		
		num =- 5;
		System.out.println("num : " + num);
		
		
	}

}
